function beep(type = 'error') {
    if (type == 'success') {
        var snd = new Audio("data:audio/wav;base64,");
        snd.play();
    }
    else {
        var snd = new Audio("data:audio/wav;base64,");
        snd.play();
    }
}
function play(data) {
    var snd = new Audio(data);
    snd.play();
}
